import React, { useState, useEffect } from 'react';
import { MapPin, Clock, Loader2, Search, Globe, Wifi, Phone, Mail, User, Calendar, Moon, Sun } from 'lucide-react';

const PROXY_API_BASE = 'http://localhost:5000/api';

export default function MawaqitApp() {
  const [location, setLocation] = useState(null);
  const [mosques, setMosques] = useState([]);
  const [selectedMosque, setSelectedMosque] = useState(null);
  const [prayerTimes, setPrayerTimes] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [language, setLanguage] = useState('en');
  const [currentTime, setCurrentTime] = useState(new Date());

  useEffect(() => {
    getUserLocation();
    const timer = setInterval(() => setCurrentTime(new Date()), 1000);
    return () => clearInterval(timer);
  }, []);

  const translations = {
    en: {
      title: "Mawaqit",
      subtitle: "Prayer Times & Mosques",
      yourLocation: "Your location",
      nearbyMosques: "Nearby Mosques",
      prayerTimes: "Prayer Times",
      within: "Within 1km of your location",
      selectMosque: "Select a mosque to view prayer times",
      today: "Today's Prayer Times",
      fajr: "Fajr",
      sunrise: "Sunrise",
      dhuhr: "Dhuhr",
      asr: "Asr",
      maghrib: "Maghrib",
      isha: "Isha",
      jumua: "Jumu'ah",
      adhan: "Adhan",
      iqama: "Iqama",
      contact: "Contact Information",
      phone: "Phone",
      email: "Email",
      website: "Website",
      facilities: "Facilities",
      away: "away",
      poweredBy: "Powered by Mawaqit",
      noMosques: "No mosques found nearby",
      errorTitle: "Error",
      makeProxy: "Make sure the proxy server is running"
    },
    ar: {
      title: "مواقيت",
      subtitle: "أوقات الصلاة والمساجد",
      yourLocation: "موقعك",
      nearbyMosques: "المساجد القريبة",
      prayerTimes: "أوقات الصلاة",
      within: "ضمن 1 كم من موقعك",
      selectMosque: "اختر مسجدًا لعرض أوقات الصلاة",
      today: "أوقات الصلاة اليوم",
      fajr: "الفجر",
      sunrise: "الشروق",
      dhuhr: "الظهر",
      asr: "العصر",
      maghrib: "المغرب",
      isha: "العشاء",
      jumua: "الجمعة",
      adhan: "الأذان",
      iqama: "الإقامة",
      contact: "معلومات الاتصال",
      phone: "الهاتف",
      email: "البريد الإلكتروني",
      website: "الموقع",
      facilities: "المرافق",
      away: "بعيدًا",
      poweredBy: "مدعوم من مواقيت",
      noMosques: "لم يتم العثور على مساجد قريبة",
      errorTitle: "خطأ",
      makeProxy: "تأكد من تشغيل الخادم الوكيل"
    }
  };

  const t = translations[language];

  const getUserLocation = () => {
    setLoading(true);
    setError('');

    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const loc = {
            lat: position.coords.latitude,
            lon: position.coords.longitude
          };
          setLocation(loc);
          fetchNearbyMosques(loc);
        },
        (err) => {
          setError(t.errorTitle + ': ' + err.message);
          setLoading(false);
        }
      );
    } else {
      setError('Geolocation is not supported');
      setLoading(false);
    }
  };

  const fetchNearbyMosques = async (loc) => {
    try {
      const url = `${PROXY_API_BASE}/mosque/search?lat=${loc.lat}&lon=${loc.lon}&distance=1`;
      const response = await fetch(url);
      if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
      const data = await response.json();
      setMosques(data || []);
      setLoading(false);
    } catch (err) {
      setError(`${t.errorTitle}: ${err.message}`);
      setLoading(false);
    }
  };

  const handleMosqueSelect = (mosque) => {
    setSelectedMosque(mosque);
    setPrayerTimes(mosque);
    setError('');
  };

  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371;
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = Math.sin(dLat/2) * Math.sin(dLat/2) +
              Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
              Math.sin(dLon/2) * Math.sin(dLon/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    return (R * c).toFixed(2);
  };

  const prayerConfig = [
    { key: 0, name: t.fajr, icon: Moon, gradient: 'from-indigo-500 via-purple-500 to-pink-500', bg: 'from-indigo-50 to-purple-50' },
    { key: 1, name: t.sunrise, icon: Sun, gradient: 'from-amber-400 via-orange-400 to-red-400', bg: 'from-amber-50 to-orange-50', noIqama: true },
    { key: 2, name: t.dhuhr, icon: Sun, gradient: 'from-yellow-400 via-amber-400 to-orange-400', bg: 'from-yellow-50 to-amber-50', iqamaIndex: 1 },
    { key: 3, name: t.asr, icon: Sun, gradient: 'from-blue-400 via-cyan-400 to-teal-400', bg: 'from-blue-50 to-cyan-50', iqamaIndex: 2 },
    { key: 4, name: t.maghrib, icon: Moon, gradient: 'from-orange-500 via-red-500 to-pink-500', bg: 'from-orange-50 to-red-50', iqamaIndex: 3 },
    { key: 5, name: t.isha, icon: Moon, gradient: 'from-purple-600 via-indigo-600 to-blue-600', bg: 'from-purple-50 to-indigo-50', iqamaIndex: 4 }
  ];

  const facilityIcons = {
    parking: '🚗',
    womenSpace: '👩',
    adultCourses: '📚',
    childrenCourses: '👶',
    ablutions: '💧',
    handicapAccessibility: '♿',
    aidPrayer: '🌙',
    janazaPrayer: '🤲',
    wifi: <Wifi size={16} className="inline" />
  };

  const getFacilities = (mosque) => {
    const facilities = [];
    if (mosque.parking) facilities.push({ icon: '🚗', text: 'Parking' });
    if (mosque.womenSpace) facilities.push({ icon: '👩', text: 'Women Space' });
    if (mosque.adultCourses) facilities.push({ icon: '📚', text: 'Adult Courses' });
    if (mosque.childrenCourses) facilities.push({ icon: '👶', text: 'Children Courses' });
    if (mosque.ablutions) facilities.push({ icon: '💧', text: 'Ablution' });
    if (mosque.handicapAccessibility) facilities.push({ icon: '♿', text: 'Accessible' });
    if (mosque.aidPrayer) facilities.push({ icon: '🌙', text: 'Eid Prayer' });
    if (mosque.janazaPrayer) facilities.push({ icon: '🤲', text: 'Janazah' });
    return facilities;
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 via-indigo-900 to-purple-950 relative overflow-hidden" dir={language === 'ar' ? 'rtl' : 'ltr'}>
      {/* Islamic Pattern Overlay */}
      <div className="absolute inset-0 opacity-5">
        <div className="absolute inset-0" style={{
          backgroundImage: `url("data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='%23ffffff' fill-opacity='1'%3E%3Cpath d='M36 34v-4h-2v4h-4v2h4v4h2v-4h4v-2h-4zm0-30V0h-2v4h-4v2h4v4h2V6h4V4h-4zM6 34v-4H4v4H0v2h4v4h2v-4h4v-2H6zM6 4V0H4v4H0v2h4v4h2V6h4V4H6z'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E")`,
        }}></div>
      </div>

      <div className="relative z-10 container mx-auto px-4 py-6 max-w-7xl">
        {/* Header */}
        <div className="text-center mb-8 relative">
          <div className="absolute top-0 right-0">
            <button
              onClick={() => setLanguage(language === 'en' ? 'ar' : 'en')}
              className="bg-white/10 backdrop-blur-md text-white px-4 py-2 rounded-full flex items-center gap-2 hover:bg-white/20 transition-all duration-300 border border-white/20"
            >
              <Globe size={18} />
              <span className="font-medium">{language === 'en' ? 'العربية' : 'English'}</span>
            </button>
          </div>

          <div className="inline-block mb-4">
            <div className="relative">
              <div className="absolute inset-0 bg-gradient-to-r from-purple-400 to-pink-400 blur-2xl opacity-50 rounded-full"></div>
              <h1 className="relative text-6xl font-bold text-white mb-2 tracking-tight" style={{ fontFamily: language === 'ar' ? 'Cairo, sans-serif' : 'Inter, sans-serif' }}>
                {t.title}
              </h1>
            </div>
          </div>
          <p className="text-purple-200 text-lg font-medium">{t.subtitle}</p>

          {/* Live Clock */}
          <div className="mt-4 text-white/80 text-sm">
            <Clock className="inline mr-2" size={16} />
            {currentTime.toLocaleTimeString(language === 'ar' ? 'ar-SA' : 'en-US', { hour: '2-digit', minute: '2-digit', second: '2-digit' })}
          </div>
        </div>

        {/* Error Alert */}
        {error && (
          <div className="bg-red-500/20 backdrop-blur-md border border-red-500/50 text-white px-6 py-4 rounded-2xl mb-6 animate-fade-in">
            <p className="font-semibold flex items-center gap-2">
              ⚠️ {t.errorTitle}
            </p>
            <p className="text-sm mt-1 text-red-100">{error}</p>
          </div>
        )}

        {/* Location Info */}
        {location && (
          <div className="bg-gradient-to-r from-purple-500/20 to-indigo-500/20 backdrop-blur-md border border-white/20 text-white px-6 py-4 rounded-2xl mb-6">
            <p className="text-sm flex items-center gap-2">
              <MapPin size={18} className="text-purple-300" />
              <span className="font-medium">{t.yourLocation}:</span>
              <span className="text-purple-200">
                {location.lat.toFixed(4)}, {location.lon.toFixed(4)}
              </span>
            </p>
          </div>
        )}

        {/* Main Content */}
        <div className="grid lg:grid-cols-5 gap-6">
          {/* Mosques List */}
          <div className="lg:col-span-2 bg-white/10 backdrop-blur-xl rounded-3xl shadow-2xl p-6 border border-white/20">
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-2xl font-bold text-white flex items-center gap-3">
                <div className="p-2 bg-gradient-to-br from-purple-500 to-pink-500 rounded-xl">
                  <MapPin size={24} />
                </div>
                {t.nearbyMosques}
              </h2>
              {loading && <Loader2 className="animate-spin text-purple-300" size={24} />}
            </div>

            {location && (
              <p className="text-purple-200 text-sm mb-4">{t.within}</p>
            )}

            <div className="space-y-3 max-h-[600px] overflow-y-auto pr-2 custom-scrollbar">
              {mosques.length === 0 && !loading && location && (
                <div className="text-center py-12">
                  <Search className="mx-auto mb-4 text-purple-300" size={48} />
                  <p className="text-purple-200">{t.noMosques}</p>
                </div>
              )}

              {mosques.map((mosque) => {
                const facilities = getFacilities(mosque);
                return (
                  <button
                    key={mosque.uuid}
                    onClick={() => handleMosqueSelect(mosque)}
                    className={`w-full text-left p-5 rounded-2xl transition-all duration-300 group ${
                      selectedMosque?.uuid === mosque.uuid
                        ? 'bg-gradient-to-r from-purple-500 to-pink-500 shadow-lg scale-105'
                        : 'bg-white/5 hover:bg-white/10 border border-white/10 hover:border-white/20'
                    }`}
                  >
                    <h3 className="font-bold text-white mb-2 text-lg group-hover:text-purple-100 transition-colors">
                      {mosque.label || mosque.name}
                    </h3>
                    <p className="text-sm text-purple-200 mb-3">
                      {mosque.localisation}
                    </p>

                    {facilities.length > 0 && (
                      <div className="flex flex-wrap gap-2 mb-2">
                        {facilities.slice(0, 4).map((facility, idx) => (
                          <span key={idx} className="text-xs bg-white/10 px-2 py-1 rounded-lg">
                            {facility.icon} {facility.text}
                          </span>
                        ))}
                        {facilities.length > 4 && (
                          <span className="text-xs bg-white/10 px-2 py-1 rounded-lg">
                            +{facilities.length - 4}
                          </span>
                        )}
                      </div>
                    )}

                    {location && mosque.latitude && mosque.longitude && (
                      <p className="text-xs text-purple-300 font-medium flex items-center gap-1">
                        <MapPin size={12} />
                        {calculateDistance(location.lat, location.lon, mosque.latitude, mosque.longitude)} km {t.away}
                      </p>
                    )}
                  </button>
                );
              })}
            </div>
          </div>

          {/* Prayer Times */}
          <div className="lg:col-span-3 bg-white/10 backdrop-blur-xl rounded-3xl shadow-2xl p-6 border border-white/20">
            <h2 className="text-2xl font-bold text-white mb-6 flex items-center gap-3">
              <div className="p-2 bg-gradient-to-br from-pink-500 to-purple-500 rounded-xl">
                <Clock size={24} />
              </div>
              {t.prayerTimes}
            </h2>

            {!selectedMosque && (
              <div className="text-center py-20">
                <Search className="mx-auto mb-6 text-purple-300" size={64} />
                <p className="text-purple-200 text-lg">{t.selectMosque}</p>
              </div>
            )}

            {prayerTimes && (
              <div className="space-y-4">
                {/* Mosque Header */}
                <div className="bg-gradient-to-r from-purple-500/20 to-pink-500/20 rounded-2xl p-6 border border-white/20 mb-6">
                  <h3 className="font-bold text-2xl text-white mb-2">
                    {prayerTimes.label || prayerTimes.name}
                  </h3>
                  <p className="text-purple-200 mb-4">
                    {prayerTimes.localisation}
                  </p>

                  {/* Facilities Grid */}
                  {getFacilities(prayerTimes).length > 0 && (
                    <div>
                      <h4 className="text-white font-semibold mb-3 text-sm">{t.facilities}</h4>
                      <div className="grid grid-cols-2 gap-2">
                        {getFacilities(prayerTimes).map((facility, idx) => (
                          <div key={idx} className="bg-white/10 px-3 py-2 rounded-lg text-sm text-white flex items-center gap-2">
                            <span>{facility.icon}</span>
                            <span>{facility.text}</span>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}
                </div>

                {/* Prayer Times Grid */}
                {prayerTimes.times && prayerTimes.times.length >= 6 && (
                  <div className="space-y-3">
                    <p className="text-purple-200 mb-4 flex items-center gap-2">
                      <Calendar size={18} />
                      {t.today}
                    </p>

                    {prayerConfig.map((prayer) => {
                      const Icon = prayer.icon;
                      return (
                        <div
                          key={prayer.key}
                          className={`bg-gradient-to-r ${prayer.bg} rounded-2xl p-5 shadow-lg hover:shadow-xl transition-all duration-300 border border-white/30 hover:scale-102`}
                        >
                          <div className="flex items-center justify-between">
                            <div className="flex items-center gap-4">
                              <div className={`p-3 bg-gradient-to-br ${prayer.gradient} rounded-xl shadow-lg`}>
                                <Icon size={24} className="text-white" />
                              </div>
                              <div>
                                <p className="font-bold text-gray-800 text-lg">{prayer.name}</p>
                                <div className="text-sm text-gray-600 mt-1 flex gap-4">
                                  <span className="flex items-center gap-1">
                                    <span className="text-xs">{t.adhan}:</span>
                                    <span className="font-bold text-lg text-gray-900">
                                      {prayerTimes.times[prayer.key]}
                                    </span>
                                  </span>
                                  {!prayer.noIqama && prayerTimes.iqama && prayerTimes.iqama[prayer.iqamaIndex || prayer.key] && (
                                    <span className="flex items-center gap-1">
                                      <span className="text-xs">{t.iqama}:</span>
                                      <span className="font-semibold text-base text-purple-700">
                                        {prayerTimes.iqama[prayer.iqamaIndex || prayer.key]}
                                      </span>
                                    </span>
                                  )}
                                </div>
                              </div>
                            </div>
                          </div>
                        </div>
                      );
                    })}

                    {/* Jumua */}
                    {prayerTimes.jumua && (
                      <div className="bg-gradient-to-r from-green-50 to-emerald-50 rounded-2xl p-5 shadow-lg border border-green-200">
                        <div className="flex items-center gap-4">
                          <div className="p-3 bg-gradient-to-br from-green-500 to-emerald-500 rounded-xl shadow-lg">
                            <Calendar size={24} className="text-white" />
                          </div>
                          <div>
                            <p className="font-bold text-gray-800 text-lg">{t.jumua}</p>
                            <span className="font-bold text-2xl text-green-700">
                              {prayerTimes.jumua}
                            </span>
                          </div>
                        </div>
                      </div>
                    )}
                  </div>
                )}

                {/* Contact Info */}
                {(prayerTimes.phone || prayerTimes.email || prayerTimes.site) && (
                  <div className="mt-6 bg-white/5 rounded-2xl p-6 border border-white/10">
                    <h4 className="font-bold text-white mb-4 flex items-center gap-2">
                      <Phone size={18} />
                      {t.contact}
                    </h4>
                    <div className="space-y-3 text-sm">
                      {prayerTimes.phone && (
                        <a href={`tel:${prayerTimes.phone}`} className="flex items-center gap-3 text-purple-200 hover:text-white transition-colors">
                          <Phone size={16} />
                          {prayerTimes.phone}
                        </a>
                      )}
                      {prayerTimes.email && (
                        <a href={`mailto:${prayerTimes.email}`} className="flex items-center gap-3 text-purple-200 hover:text-white transition-colors">
                          <Mail size={16} />
                          {prayerTimes.email}
                        </a>
                      )}
                      {prayerTimes.site && (
                        <a href={prayerTimes.site} target="_blank" rel="noopener noreferrer" className="flex items-center gap-3 text-purple-200 hover:text-white transition-colors">
                          <Globe size={16} />
                          {prayerTimes.site}
                        </a>
                      )}
                    </div>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>

        {/* Footer */}
        <div className="text-center mt-8 text-purple-300 text-sm">
          <p>{t.poweredBy}</p>
        </div>
      </div>

      <style>{`
        @import url('https://fonts.googleapis.com/css2?family=Cairo:wght@400;600;700;900&family=Inter:wght@400;500;600;700;800;900&display=swap');

        .custom-scrollbar::-webkit-scrollbar {
          width: 8px;
        }
        .custom-scrollbar::-webkit-scrollbar-track {
          background: rgba(255, 255, 255, 0.1);
          border-radius: 10px;
        }
        .custom-scrollbar::-webkit-scrollbar-thumb {
          background: rgba(168, 85, 247, 0.5);
          border-radius: 10px;
        }
        .custom-scrollbar::-webkit-scrollbar-thumb:hover {
          background: rgba(168, 85, 247, 0.7);
        }

        @keyframes fade-in {
          from { opacity: 0; transform: translateY(-10px); }
          to { opacity: 1; transform: translateY(0); }
        }
        .animate-fade-in {
          animation: fade-in 0.3s ease-out;
        }

        .hover\:scale-102:hover {
          transform: scale(1.02);
        }
      `}</style>
    </div>
  );
}
