import React, { useState, useEffect } from 'react';
import { MapPin, Clock, Loader2, Search } from 'lucide-react';

// Use your local proxy server instead of direct API calls
const PROXY_API_BASE = 'http://localhost:5000/api';

export default function MawaqitApp() {
  const [location, setLocation] = useState(null);
  const [mosques, setMosques] = useState([]);
  const [selectedMosque, setSelectedMosque] = useState(null);
  const [prayerTimes, setPrayerTimes] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    getUserLocation();
  }, []);

  const getUserLocation = () => {
    setLoading(true);
    setError('');

    console.log('Requesting location...');

    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const loc = {
            lat: position.coords.latitude,
            lon: position.coords.longitude
          };
          console.log('Location obtained:', loc);
          setLocation(loc);
          fetchNearbyMosques(loc);
        },
        (err) => {
          console.error('Location error:', err);
          setError('Unable to get your location. Please enable location services.');
          setLoading(false);
        }
      );
    } else {
      setError('Geolocation is not supported by your browser.');
      setLoading(false);
    }
  };

  const fetchNearbyMosques = async (loc) => {
    try {
      const url = `${PROXY_API_BASE}/mosque/search?lat=${loc.lat}&lon=${loc.lon}&distance=1`;
      console.log('Fetching mosques from proxy:', url);

      const response = await fetch(url);
      console.log('Response status:', response.status);

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      console.log('Mosques data:', data);

      setMosques(data || []);
      setLoading(false);
    } catch (err) {
      console.error('Fetch mosques error:', err);
      setError(`Failed to load nearby mosques: ${err.message}. Make sure the proxy server is running on port 5000.`);
      setLoading(false);
    }
  };

  const handleMosqueSelect = (mosque) => {
    console.log('Selected mosque data:', mosque);
    setSelectedMosque(mosque);
    setPrayerTimes(mosque);
    setError(''); // Clear any previous errors
  };

  const formatTime = (time) => {
    if (!time) return '--:--';
    return time;
  };

  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371;
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = Math.sin(dLat/2) * Math.sin(dLat/2) +
              Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
              Math.sin(dLon/2) * Math.sin(dLon/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    return (R * c).toFixed(2);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-emerald-50 to-teal-100">
      <div className="container mx-auto px-4 py-8 max-w-6xl">
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-emerald-800 mb-2">
            Mawaqit Prayer Times
          </h1>
          <p className="text-emerald-600">Find nearby mosques and prayer times</p>
        </div>

        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-6">
            <p className="font-semibold">Error:</p>
            <p>{error}</p>
            <p className="text-sm mt-2">
              Make sure the proxy server is running: <code className="bg-red-200 px-1">node server.js</code>
            </p>
          </div>
        )}

        {location && (
          <div className="bg-blue-100 border border-blue-400 text-blue-700 px-4 py-3 rounded mb-6">
            <p className="text-sm">
              📍 Your location: Lat {location.lat.toFixed(4)}, Lon {location.lon.toFixed(4)}
            </p>
          </div>
        )}

        <div className="grid md:grid-cols-2 gap-6">
          <div className="bg-white rounded-lg shadow-lg p-6">
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-2xl font-semibold text-gray-800 flex items-center gap-2">
                <MapPin className="text-emerald-600" />
                Nearby Mosques
              </h2>
              {loading && <Loader2 className="animate-spin text-emerald-600" />}
            </div>

            {location && (
              <p className="text-sm text-gray-500 mb-4">
                Within 1km of your location
              </p>
            )}

            <div className="space-y-3 max-h-96 overflow-y-auto">
              {mosques.length === 0 && !loading && location && (
                <div className="text-center py-8">
                  <p className="text-gray-500 mb-4">
                    No mosques found nearby within 1km.
                  </p>
                </div>
              )}

              {mosques.map((mosque) => (
                <button
                  key={mosque.uuid}
                  onClick={() => handleMosqueSelect(mosque)}
                  className={`w-full text-left p-4 rounded-lg border-2 transition-all ${
                    selectedMosque?.uuid === mosque.uuid
                      ? 'border-emerald-500 bg-emerald-50'
                      : 'border-gray-200 hover:border-emerald-300 hover:bg-gray-50'
                  }`}
                >
                  <h3 className="font-semibold text-gray-800 mb-1">
                    {mosque.label || mosque.name}
                  </h3>
                  <p className="text-sm text-gray-600">
                    {mosque.localisation}
                  </p>
                  {location && mosque.latitude && mosque.longitude && (
                    <p className="text-xs text-emerald-600 mt-1">
                      {calculateDistance(
                        location.lat,
                        location.lon,
                        mosque.latitude,
                        mosque.longitude
                      )} km away
                    </p>
                  )}
                </button>
              ))}
            </div>
          </div>

          <div className="bg-white rounded-lg shadow-lg p-6">
            <h2 className="text-2xl font-semibold text-gray-800 mb-4 flex items-center gap-2">
              <Clock className="text-emerald-600" />
              Prayer Times
            </h2>

            {!selectedMosque && (
              <div className="text-center py-12 text-gray-500">
                <Search className="mx-auto mb-4 text-gray-400" size={48} />
                <p>Select a mosque to view prayer times</p>
              </div>
            )}

            {selectedMosque && !prayerTimes && (
              <div className="text-center py-12">
                <Loader2 className="animate-spin mx-auto text-emerald-600 mb-4" size={48} />
                <p className="text-gray-600">Loading mosque details...</p>
              </div>
            )}

            {prayerTimes && (
              <div>
                <div className="mb-6 pb-4 border-b">
                  <h3 className="font-bold text-lg text-gray-800">
                    {prayerTimes.label || prayerTimes.name}
                  </h3>
                  <p className="text-sm text-gray-600">
                    {prayerTimes.localisation}
                  </p>
                </div>

                {/* Display Prayer Times from the times array */}
                {prayerTimes.times && prayerTimes.times.length >= 6 && (
                  <div className="space-y-4">
                    <p className="text-sm text-gray-500 mb-4">
                      📅 Today's Prayer Times
                    </p>

                    {/* Fajr */}
                    <div className="p-4 bg-gradient-to-r from-indigo-50 to-purple-50 rounded-lg">
                      <div className="flex-1">
                        <p className="font-semibold text-gray-800">Fajr</p>
                        <div className="text-sm text-gray-600 mt-1">
                          <span className="inline-block mr-4">
                            Adhan: <span className="font-medium">{prayerTimes.times[0]}</span>
                          </span>
                          {prayerTimes.iqama && prayerTimes.iqama[0] && (
                            <span className="inline-block">
                              Iqama: <span className="font-medium">{prayerTimes.iqama[0]}</span>
                            </span>
                          )}
                        </div>
                      </div>
                    </div>

                    {/* Sunrise */}
                    <div className="p-4 bg-gradient-to-r from-amber-50 to-yellow-50 rounded-lg">
                      <div className="flex-1">
                        <p className="font-semibold text-gray-800">Sunrise (Shuruq)</p>
                        <div className="text-sm text-gray-600 mt-1">
                          <span className="font-medium">{prayerTimes.times[1]}</span>
                        </div>
                      </div>
                    </div>

                    {/* Dhuhr */}
                    <div className="p-4 bg-gradient-to-r from-emerald-50 to-teal-50 rounded-lg">
                      <div className="flex-1">
                        <p className="font-semibold text-gray-800">Dhuhr</p>
                        <div className="text-sm text-gray-600 mt-1">
                          <span className="inline-block mr-4">
                            Adhan: <span className="font-medium">{prayerTimes.times[2]}</span>
                          </span>
                          {prayerTimes.iqama && prayerTimes.iqama[1] && (
                            <span className="inline-block">
                              Iqama: <span className="font-medium">{prayerTimes.iqama[1]}</span>
                            </span>
                          )}
                        </div>
                      </div>
                    </div>

                    {/* Asr */}
                    <div className="p-4 bg-gradient-to-r from-blue-50 to-cyan-50 rounded-lg">
                      <div className="flex-1">
                        <p className="font-semibold text-gray-800">Asr</p>
                        <div className="text-sm text-gray-600 mt-1">
                          <span className="inline-block mr-4">
                            Adhan: <span className="font-medium">{prayerTimes.times[3]}</span>
                          </span>
                          {prayerTimes.iqama && prayerTimes.iqama[2] && (
                            <span className="inline-block">
                              Iqama: <span className="font-medium">{prayerTimes.iqama[2]}</span>
                            </span>
                          )}
                        </div>
                      </div>
                    </div>

                    {/* Maghrib */}
                    <div className="p-4 bg-gradient-to-r from-orange-50 to-red-50 rounded-lg">
                      <div className="flex-1">
                        <p className="font-semibold text-gray-800">Maghrib</p>
                        <div className="text-sm text-gray-600 mt-1">
                          <span className="inline-block mr-4">
                            Adhan: <span className="font-medium">{prayerTimes.times[4]}</span>
                          </span>
                          {prayerTimes.iqama && prayerTimes.iqama[3] && (
                            <span className="inline-block">
                              Iqama: <span className="font-medium">{prayerTimes.iqama[3]}</span>
                            </span>
                          )}
                        </div>
                      </div>
                    </div>

                    {/* Isha */}
                    <div className="p-4 bg-gradient-to-r from-slate-50 to-gray-50 rounded-lg">
                      <div className="flex-1">
                        <p className="font-semibold text-gray-800">Isha</p>
                        <div className="text-sm text-gray-600 mt-1">
                          <span className="inline-block mr-4">
                            Adhan: <span className="font-medium">{prayerTimes.times[5]}</span>
                          </span>
                          {prayerTimes.iqama && prayerTimes.iqama[4] && (
                            <span className="inline-block">
                              Iqama: <span className="font-medium">{prayerTimes.iqama[4]}</span>
                            </span>
                          )}
                        </div>
                      </div>
                    </div>

                    {/* Jumua */}
                    {prayerTimes.jumua && (
                      <div className="p-4 bg-gradient-to-r from-green-50 to-emerald-50 rounded-lg mt-4">
                        <div className="flex-1">
                          <p className="font-semibold text-gray-800">Jumu'ah (Friday Prayer)</p>
                          <p className="text-sm text-gray-600 mt-1">
                            <span className="font-medium">{prayerTimes.jumua}</span>
                          </p>
                        </div>
                      </div>
                    )}
                  </div>
                )}

                {/* Additional mosque info */}
                {(prayerTimes.email || prayerTimes.phone || prayerTimes.site) && (
                  <div className="mt-6 pt-6 border-t">
                    <h4 className="font-semibold text-gray-800 mb-3">Contact Information</h4>
                    <div className="space-y-2 text-sm text-gray-700">
                      {prayerTimes.phone && (
                        <p>📞 Phone: {prayerTimes.phone}</p>
                      )}
                      {prayerTimes.email && (
                        <p>✉️ Email: {prayerTimes.email}</p>
                      )}
                      {prayerTimes.site && (
                        <p>
                          🌐 Website:{' '}
                          <a href={prayerTimes.site} target="_blank" rel="noopener noreferrer" className="text-emerald-600 hover:underline">
                            {prayerTimes.site}
                          </a>
                        </p>
                      )}
                      {prayerTimes.url && (
                        <p>
                          🕌 Mawaqit Page:{' '}
                          <a href={prayerTimes.url} target="_blank" rel="noopener noreferrer" className="text-emerald-600 hover:underline">
                            View on Mawaqit
                          </a>
                        </p>
                      )}
                    </div>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>

        <div className="text-center mt-8 text-sm text-gray-600">
          <p>Powered by Mawaqit API (via proxy)</p>
        </div>
      </div>
    </div>
  );
}
